import CommerceLayerContext from"../context/CommerceLayerContext";import getSdk from"../utils/getSdk";import{useContext}from"react";export function useCommerceLayer(){const ctx=useContext(CommerceLayerContext);if("accessToken"in ctx)return{accessToken:ctx.accessToken,sdkClient:()=>{if(ctx?.accessToken!=null&&ctx?.endpoint!=null)return getSdk({accessToken:ctx.accessToken,endpoint:ctx.endpoint})}};throw new Error("Cannot use `useCommerceLayer` outside of <CommerceLayer/>")}export default useCommerceLayer;